import React, { useState, useEffect, useCallback } from 'react';
import { 
  Container, 
  AppBar, 
  Toolbar, 
  Typography, 
  Box, 
  Card, 
  CardContent, 
  TextField, 
  Button, 
  Grid, 
  Paper,
  CircularProgress,
  Alert,
  Chip,
  LinearProgress,
  Divider
} from '@mui/material';
import { 
  CloudUpload, 
  VideoLibrary, 
  TextFields, 
  RecordVoiceOver, 
  Image,
  CheckCircle,
  Error,
  Warning,
  Wifi,
  WifiOff,
  Movie,
  Upload,
  Analytics
} from '@mui/icons-material';
import './App.css';

// Configuration de l'API
const API_BASE_URL = 'http://127.0.0.1:8000';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('testing');
  const [uploadProgress, setUploadProgress] = useState(0);

  // Tester la connexion au backend au démarrage
  useEffect(() => {
    testConnection();
  }, []);

  const testConnection = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/test`);
      if (response.ok) {
        setConnectionStatus('connected');
      } else {
        setConnectionStatus('disconnected');
      }
    } catch (err) {
      setConnectionStatus('disconnected');
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Vérifier que c'est un fichier vidéo
      if (file.type.startsWith('video/')) {
        setSelectedFile(file);
        setError('');
      } else {
        setError('Veuillez sélectionner un fichier vidéo valide');
        setSelectedFile(null);
      }
    }
  };

  const handleAnalyze = async () => {
    if (!selectedFile) {
      setError('Veuillez sélectionner un fichier vidéo');
      return;
    }

    setLoading(true);
    setError('');
    setUploadProgress(0);
    
    try {
      const formData = new FormData();
      formData.append('file', selectedFile);

      const response = await fetch(`${API_BASE_URL}/upload-video`, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Erreur HTTP: ${response.status}`);
      }

      const result = await response.json();
      setAnalysis(result);
      
    } catch (err) {
      setError(`Erreur lors de l'analyse: ${err.message}`);
    } finally {
      setLoading(false);
      setUploadProgress(0);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'conforme': return 'success';
      case 'attention': return 'warning';
      case 'non-conforme': return 'error';
      default: return 'default';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'conforme': return <CheckCircle />;
      case 'attention': return <Warning />;
      case 'non-conforme': return <Error />;
      default: return null;
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div className="app">
      <AppBar position="static" sx={{ backgroundColor: '#2c3e50' }}>
        <Toolbar>
          <Movie sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Analyseur de Contenu Vidéo Local
          </Typography>
          
          {/* Indicateur de connexion */}
          <Box display="flex" alignItems="center">
            {connectionStatus === 'connected' ? (
              <Chip 
                icon={<Wifi />} 
                label="Connecté" 
                color="success" 
                variant="filled" 
                size="small"
              />
            ) : connectionStatus === 'disconnected' ? (
              <Chip 
                icon={<WifiOff />} 
                label="Déconnecté" 
                color="error" 
                variant="filled" 
                size="small"
              />
            ) : (
              <CircularProgress size={20} color="inherit" />
            )}
          </Box>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ mt: 4, pb: 4 }}>
        {/* Test de connexion */}
        {connectionStatus === 'disconnected' && (
          <Alert severity="error" sx={{ mb: 4 }} action={
            <Button color="inherit" size="small" onClick={testConnection}>
              Reconnect
            </Button>
          }>
            Impossible de se connecter au backend FastAPI. Assurez-vous que le serveur est démarré sur http://localhost:8000
          </Alert>
        )}

        {connectionStatus === 'connected' && (
          <Alert severity="success" sx={{ mb: 4 }}>
            ✅ Système d'analyse vidéo prêt ! Upload et analysez vos fichiers vidéo locaux.
          </Alert>
        )}

        {/* Section d'upload */}
        <Card sx={{ mb: 4, backgroundColor: 'rgba(255,255,255,0.95)' }}>
          <CardContent>
            <Typography variant="h5" gutterBottom color="primary">
              � Analyser un fichier vidéo local
            </Typography>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Uploadez votre fichier vidéo pour analyser son contenu : audio, images et texte
            </Typography>
            
            <Box sx={{ mt: 3 }}>
              {/* Input file */}
              <Box sx={{ mb: 2 }}>
                <input
                  accept="video/*"
                  style={{ display: 'none' }}
                  id="video-upload"
                  type="file"
                  onChange={handleFileChange}
                />
                <label htmlFor="video-upload">
                  <Button
                    variant="outlined"
                    component="span"
                    startIcon={<Upload />}
                    sx={{ mr: 2 }}
                    disabled={connectionStatus !== 'connected'}
                  >
                    Choisir un fichier vidéo
                  </Button>
                </label>
                
                {selectedFile && (
                  <Chip 
                    label={`${selectedFile.name} (${formatFileSize(selectedFile.size)})`}
                    color="primary"
                    variant="outlined"
                  />
                )}
              </Box>
              
              <Button
                variant="contained"
                startIcon={loading ? <CircularProgress size={20} /> : <Analytics />}
                onClick={handleAnalyze}
                disabled={loading || !selectedFile || connectionStatus !== 'connected'}
                size="large"
                sx={{ 
                  background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
                  color: 'white'
                }}
              >
                {loading ? 'Analyse en cours...' : 'Analyser la vidéo'}
              </Button>
            </Box>

            {error && (
              <Alert severity="error" sx={{ mt: 2 }}>
                {error}
              </Alert>
            )}
          </CardContent>
        </Card>

        {/* Barre de progression */}
        {loading && (
          <Paper sx={{ p: 2, mb: 4, backgroundColor: 'rgba(255,255,255,0.95)' }}>
            <Typography variant="h6" gutterBottom>
              Analyse en cours...
            </Typography>
            <LinearProgress sx={{ mb: 1 }} />
            <Typography variant="body2" color="text.secondary">
              Extraction et analyse : audio, images, texte et métadonnées...
            </Typography>
          </Paper>
        )}

        {/* Résultats d'analyse */}
        {analysis && (
          <>
            {/* Informations vidéo */}
            <Card sx={{ mb: 3, backgroundColor: 'rgba(255,255,255,0.95)' }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  📊 Informations de la vidéo
                </Typography>
                <Grid container spacing={2}>
                  <Grid item xs={6} md={3}>
                    <Typography variant="body2" color="text.secondary">Durée</Typography>
                    <Typography variant="body1">{analysis.video_info?.duration || 'N/A'}s</Typography>
                  </Grid>
                  <Grid item xs={6} md={3}>
                    <Typography variant="body2" color="text.secondary">Résolution</Typography>
                    <Typography variant="body1">{analysis.video_info?.resolution || 'N/A'}</Typography>
                  </Grid>
                  <Grid item xs={6} md={3}>
                    <Typography variant="body2" color="text.secondary">FPS</Typography>
                    <Typography variant="body1">{analysis.video_info?.fps || 'N/A'}</Typography>
                  </Grid>
                  <Grid item xs={6} md={3}>
                    <Typography variant="body2" color="text.secondary">Temps d'analyse</Typography>
                    <Typography variant="body1">{analysis.processing_time?.toFixed(2) || 'N/A'}s</Typography>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>

            <Grid container spacing={3}>
              {/* Analyse Audio/Voix */}
              <Grid item xs={12} md={6}>
                <Card sx={{ backgroundColor: 'rgba(255,255,255,0.95)', height: '100%' }}>
                  <CardContent>
                    <Box display="flex" alignItems="center" mb={2}>
                      <RecordVoiceOver color="primary" sx={{ mr: 1 }} />
                      <Typography variant="h6">Analyse Audio/Voix</Typography>
                    </Box>
                    
                    <Box display="flex" alignItems="center" mb={2}>
                      <Chip
                        icon={getStatusIcon(analysis.voice?.status || 'attention')}
                        label={(analysis.voice?.status || 'N/A').toUpperCase()}
                        color={getStatusColor(analysis.voice?.status || 'attention')}
                        variant="filled"
                      />
                      <Typography variant="h4" sx={{ ml: 2 }}>
                        {analysis.voice?.score || 'N/A'}%
                      </Typography>
                    </Box>
                    
                    <Typography variant="body2" color="text.secondary">
                      Résultats :
                    </Typography>
                    {analysis.voice?.issues?.map((issue, index) => (
                      <Typography key={index} variant="body2" sx={{ mt: 1 }}>
                        • {issue}
                      </Typography>
                    ))}
                  </CardContent>
                </Card>
              </Grid>

              {/* Analyse Images */}
              <Grid item xs={12} md={6}>
                <Card sx={{ backgroundColor: 'rgba(255,255,255,0.95)', height: '100%' }}>
                  <CardContent>
                    <Box display="flex" alignItems="center" mb={2}>
                      <Image color="primary" sx={{ mr: 1 }} />
                      <Typography variant="h6">Analyse des Images</Typography>
                    </Box>
                    
                    <Box display="flex" alignItems="center" mb={2}>
                      <Chip
                        icon={getStatusIcon(analysis.image?.status || 'attention')}
                        label={(analysis.image?.status || 'N/A').toUpperCase()}
                        color={getStatusColor(analysis.image?.status || 'attention')}
                        variant="filled"
                      />
                      <Typography variant="h4" sx={{ ml: 2 }}>
                        {analysis.image?.score || 'N/A'}%
                      </Typography>
                    </Box>
                    
                    <Typography variant="body2" color="text.secondary">
                      Frames analysées: {analysis.image?.details?.frames_analyzed || 0}
                    </Typography>
                    {analysis.image?.issues?.map((issue, index) => (
                      <Typography key={index} variant="body2" sx={{ mt: 1 }}>
                        • {issue}
                      </Typography>
                    ))}
                  </CardContent>
                </Card>
              </Grid>

              {/* Analyse Texte */}
              <Grid item xs={12} md={6}>
                <Card sx={{ backgroundColor: 'rgba(255,255,255,0.95)', height: '100%' }}>
                  <CardContent>
                    <Box display="flex" alignItems="center" mb={2}>
                      <TextFields color="primary" sx={{ mr: 1 }} />
                      <Typography variant="h6">Analyse du Texte</Typography>
                    </Box>
                    
                    <Box display="flex" alignItems="center" mb={2}>
                      <Chip
                        icon={getStatusIcon(analysis.title?.status || 'attention')}
                        label={(analysis.title?.status || 'N/A').toUpperCase()}
                        color={getStatusColor(analysis.title?.status || 'attention')}
                        variant="filled"
                      />
                      <Typography variant="h4" sx={{ ml: 2 }}>
                        {analysis.title?.score || 'N/A'}%
                      </Typography>
                    </Box>
                    
                    <Typography variant="body2" color="text.secondary">
                      Résultats :
                    </Typography>
                    {analysis.title?.issues?.map((issue, index) => (
                      <Typography key={index} variant="body2" sx={{ mt: 1 }}>
                        • {issue}
                      </Typography>
                    ))}
                  </CardContent>
                </Card>
              </Grid>

              {/* Résultat global et Compliance Report */}
              <Grid item xs={12}>
                <Card sx={{ backgroundColor: 'rgba(255,255,255,0.95)' }}>
                  <CardContent>
                    <Box display="flex" alignItems="center" mb={2}>
                      <Analytics color="primary" sx={{ mr: 1 }} />
                      <Typography variant="h6">Rapport de Conformité YouTube</Typography>
                    </Box>
                    
                    {/* Score global pondéré */}
                    <Box display="flex" alignItems="center" mb={3}>
                      <Typography variant="h4" sx={{ mr: 2 }}>
                        Score Global: {analysis.compliance_report?.weighted_score || 'N/A'}%
                      </Typography>
                      <Chip
                        icon={getStatusIcon((analysis.overall && analysis.overall.includes('CONFORME')) ? 'conforme' : 
                               (analysis.overall && analysis.overall.includes('ATTENTION')) ? 'attention' : 'non-conforme')}
                        label={analysis.overall || 'N/A'}
                        color={getStatusColor((analysis.overall && analysis.overall.includes('CONFORME')) ? 'conforme' : 
                               (analysis.overall && analysis.overall.includes('ATTENTION')) ? 'attention' : 'non-conforme')}
                        variant="filled"
                        sx={{ fontSize: '1rem', padding: '8px 16px' }}
                      />
                    </Box>

                    {/* Scores individuels */}
                    <Typography variant="h6" sx={{ mb: 2 }}>Scores par Catégorie:</Typography>
                    <Grid container spacing={2} sx={{ mb: 3 }}>
                      <Grid item xs={4}>
                        <Box textAlign="center" p={2} border={1} borderColor="grey.300" borderRadius={2}>
                          <Typography variant="h5" color="primary">
                            {analysis.compliance_report?.individual_scores?.audio || analysis.voice?.score || 'N/A'}%
                          </Typography>
                          <Typography variant="body2">Audio/Voix (35%)</Typography>
                        </Box>
                      </Grid>
                      <Grid item xs={4}>
                        <Box textAlign="center" p={2} border={1} borderColor="grey.300" borderRadius={2}>
                          <Typography variant="h5" color="primary">
                            {analysis.compliance_report?.individual_scores?.image || analysis.image?.score || 'N/A'}%
                          </Typography>
                          <Typography variant="body2">Images (40%)</Typography>
                        </Box>
                      </Grid>
                      <Grid item xs={4}>
                        <Box textAlign="center" p={2} border={1} borderColor="grey.300" borderRadius={2}>
                          <Typography variant="h5" color="primary">
                            {analysis.compliance_report?.individual_scores?.text || analysis.title?.score || 'N/A'}%
                          </Typography>
                          <Typography variant="body2">Texte (25%)</Typography>
                        </Box>
                      </Grid>
                    </Grid>

                    {/* Violations critiques */}
                    {analysis.compliance_report?.critical_violations?.length > 0 && (
                      <Box sx={{ mb: 3 }}>
                        <Typography variant="h6" color="error" sx={{ mb: 1 }}>
                          ⚠️ Violations Critiques:
                        </Typography>
                        {analysis.compliance_report.critical_violations.map((violation, index) => (
                          <Alert severity="error" key={index} sx={{ mb: 1 }}>
                            {violation}
                          </Alert>
                        ))}
                      </Box>
                    )}

                    {/* Recommandations */}
                    {analysis.compliance_report?.recommendations?.length > 0 && (
                      <Box sx={{ mb: 3 }}>
                        <Typography variant="h6" color="warning.main" sx={{ mb: 1 }}>
                          📋 Recommandations:
                        </Typography>
                        {analysis.compliance_report.recommendations.map((rec, index) => (
                          <Alert severity="warning" key={index} sx={{ mb: 1 }}>
                            {rec}
                          </Alert>
                        ))}
                      </Box>
                    )}

                    {/* Informations détaillées */}
                    <Typography variant="h6" sx={{ mb: 2 }}>Détails de l'Analyse:</Typography>
                    <Grid container spacing={2}>
                      <Grid item xs={12} md={6}>
                        <Typography variant="body2" color="text.secondary">
                          <strong>Durée:</strong> {analysis.video_info?.duration || 'N/A'}s
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          <strong>Résolution:</strong> {analysis.video_info?.resolution || 'N/A'}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          <strong>FPS:</strong> {analysis.video_info?.fps || 'N/A'}
                        </Typography>
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Typography variant="body2" color="text.secondary">
                          <strong>Frames analysées:</strong> {analysis.image?.details?.frames_analyzed || 'N/A'}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          <strong>Audio analysé:</strong> {analysis.voice?.details?.has_audio ? 'Oui' : 'Non'}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          <strong>Temps de traitement:</strong> {analysis.processing_time?.toFixed(2) || 'N/A'}s
                        </Typography>
                      </Grid>
                    </Grid>
                  </CardContent>
                </Card>
              </Grid>

              {/* Détails par catégorie */}
              <Grid item xs={12}>
                <Typography variant="h5" sx={{ mb: 3, textAlign: 'center' }}>
                  Analyse Détaillée par Catégorie
                </Typography>
              </Grid>

              {/* Résultat global */}
              <Grid item xs={12} md={6}>
                <Card sx={{ backgroundColor: 'rgba(255,255,255,0.95)', height: '100%' }}>
                  <CardContent>
                    <Typography variant="h5" gutterBottom>
                      🎯 Résultat Global
                    </Typography>
                    <Box display="flex" alignItems="center" mb={2}>
                      <Chip
                        icon={getStatusIcon((analysis.overall && analysis.overall.includes('CONFORME')) ? 'conforme' : 
                               (analysis.overall && analysis.overall.includes('ATTENTION')) ? 'attention' : 'non-conforme')}
                        label={analysis.overall || 'N/A'}
                        color={getStatusColor((analysis.overall && analysis.overall.includes('CONFORME')) ? 'conforme' : 
                               (analysis.overall && analysis.overall.includes('ATTENTION')) ? 'attention' : 'non-conforme')}
                        variant="filled"
                        size="large"
                      />
                    </Box>
                    <Typography variant="body1" sx={{ mb: 2 }}>
                      Score global pondéré: {analysis.compliance_report?.weighted_score || 'N/A'}%
                    </Typography>
                    <Typography variant="body2" sx={{ mb: 1 }}>
                      {analysis.overall || 'N/A'}
                    </Typography>
                    <Divider sx={{ my: 2 }} />
                    <Typography variant="body2" color="text.secondary">
                      Scores détaillés :
                    </Typography>
                    <Typography variant="body2">Audio: {analysis.voice?.score || 'N/A'}% (35%)</Typography>
                    <Typography variant="body2">Images: {analysis.image?.score || 'N/A'}% (40%)</Typography>
                    <Typography variant="body2">Texte: {analysis.title?.score || 'N/A'}% (25%)</Typography>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
          </>
        )}
      </Container>
    </div>
  );
}

export default App;
