import React, { useState, useEffect } from 'react';
import RagAnalysisPage from './RagAnalysisPage';
import SettingsPage from './SettingsPage';
import {
  Container,
  AppBar,
  Toolbar,
  Typography,
  Box,
  Button,
  LinearProgress,
  Paper,
  Grid
} from '@mui/material';
import VideoLibrary from '@mui/icons-material/VideoLibrary';
import CheckCircle from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';
import Analytics from '@mui/icons-material/Analytics';

const API_BASE_URL = 'http://localhost:8000';

// Helper component to parse and display the comprehensive report interactively
function ComprehensiveReportDisplay({ report }) {
  if (!report) return null;
  return (
    <Paper sx={{ p: 2, mb: 2 }}>
      <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap' }}>{report}</Typography>
    </Paper>
  );
}

function App() {
  // Progress bar state
  const [progress, setProgress] = useState(0);
  // RAG analysis state
  const [showRagPage, setShowRagPage] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [ragAnalysis, setRagAnalysis] = useState(null);
  const [report, setReport] = useState(null);
  const [showSidebar, setShowSidebar] = useState(true);
  // Upload history logic moved to UploadHistoryPage.js

  const [selectedFile, setSelectedFile] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  // Parsed fields from the raw report
  const [parsedReport, setParsedReport] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('testing');
  // Frame extraction settings state
  const [frameSettings, setFrameSettings] = useState({
    frameCount: 20,
    frameInterval: 0.5,
    frameResolution: 'auto',
    frameFormat: 'jpg',
    frameStart: 0,
    frameEnd: null,
    frameSampling: 'interval',
  });

  // Test connection on startup
  useEffect(() => {
    testConnection();
  }, []);

  const testConnection = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      if (response.ok) {
        setConnectionStatus('connected');
      } else {
        setConnectionStatus('disconnected');
      }
    } catch (err) {
      setConnectionStatus('disconnected');
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.type.startsWith('video/')) {
        setSelectedFile(file);
        setError('');
        setAnalysis(null); // Clear previous results
      } else {
        setError('Please select a valid video file');
        setSelectedFile(null);
      }
    }
  };

  useEffect(() => {
    let timer;
    if (loading) {
      setProgress(0);
      timer = setInterval(() => {
        setProgress((old) => (old < 95 ? old + 5 : old));
      }, 500);
    } else {
      setProgress(100);
    }
    return () => clearInterval(timer);
  }, [loading]);

  const handleAnalyze = async () => {
    if (!selectedFile) {
      setError('Please select a video file');
      return;
    }

    setLoading(true);
    setError('');
    setAnalysis(null);

    // Helper to robustly parse the raw report string into structured fields
    function parseReportFields(report) {
      if (!report) return {};
      // Helper to extract a block by header (case-insensitive, flexible)
      const extractBlock = (header) => {
        const regex = new RegExp(`${header}[\s\S]*?(?=\n\n|$|Text Analysis|Audio Analysis|Video Frame Analysis|Overall YouTube Compliance Report)`, 'i');
        const match = report.match(regex);
        return match ? match[0] : '';
      };
      // Helper to extract a value by regex from a block
      const get = (block, regex) => {
        const m = block.match(regex);
        return m ? m[1].trim() : undefined;
      };
      // Helper to extract issues/results (multi-line, after 'Results:')
      const getIssues = (block) => {
        const m = block.match(/Results:\s*([\s\S]*?)(?=\n\w|\n$|$)/i);
        if (m) {
          // Remove any trailing section headers or extra whitespace
          let issues = m[1].replace(/(={2,}|-{2,}|\*{2,}|Text Analysis|Audio Analysis|Video Frame Analysis|Overall YouTube Compliance Report)[\s\S]*$/i, '').trim();
          // Split by bullet or newline, filter empty
          return issues.split(/\n|â€¢/).map(i => i.trim()).filter(i => i);
        }
        return [];
      };
      // Extract blocks
      const textBlock = extractBlock('Text Analysis');
      const audioBlock = extractBlock('Audio Analysis');
      const imageBlock = extractBlock('Video Frame Analysis');
      const overallBlock = extractBlock('Overall YouTube Compliance Report');
      // Main fields
      return {
        duration: get(report, /Duration\s*:?\s*([\d\.]+)/i),
        resolution: get(report, /Resolution\s*:?\s*([\dx]+)/i),
        fps: get(report, /FPS\s*:?\s*([\d\.]+)/i),
        processing_time: get(report, /Processing Time\s*:?\s*([\d\.]+)/i),
        text_score: get(textBlock, /Score\s*:?\s*(\d+\.?\d*)%/i) || get(textBlock, /(\d+\.?\d*)%/i),
        text_status: get(textBlock, /Status\s*:?\s*(conforme|attention|non_conforme|N\/A|MINOR_VIOLATIONS)/i) || get(textBlock, /(conforme|attention|non_conforme|N\/A|MINOR_VIOLATIONS)/i),
        text_issues: getIssues(textBlock),
        audio_score: get(audioBlock, /Score\s*:?\s*(\d+\.?\d*)%/i) || get(audioBlock, /(\d+\.?\d*)%/i),
        audio_status: get(audioBlock, /Status\s*:?\s*(conforme|attention|non_conforme|N\/A|MINOR_VIOLATIONS)/i) || get(audioBlock, /(conforme|attention|non_conforme|N\/A|MINOR_VIOLATIONS)/i),
        audio_issues: getIssues(audioBlock),
        image_score: get(imageBlock, /Score\s*:?\s*(\d+\.?\d*)%/i) || get(imageBlock, /(\d+\.?\d*)%/i),
        image_status: get(imageBlock, /Status\s*:?\s*(conforme|attention|non_conforme|N\/A|MINOR_VIOLATIONS)/i) || get(imageBlock, /(conforme|attention|non_conforme|N\/A|MINOR_VIOLATIONS)/i),
        image_issues: getIssues(imageBlock),
        frames_analyzed: get(imageBlock, /Frames analyzed\s*:?\s*(\d+)/i),
        overall: get(overallBlock, /Overall Status\s*:?\s*(conforme|attention|non_conforme|N\/A|MINOR_VIOLATIONS)/i) || get(overallBlock, /(conforme|attention|non_conforme|N\/A|MINOR_VIOLATIONS)/i),
        overall_scores: {
          text: get(overallBlock, /Text Content \(20%\)\s*(\d+\.?\d*)%/i),
          audio: get(overallBlock, /Audio Content \(35%\)\s*(\d+\.?\d*)%/i),
          image: get(overallBlock, /Video Frames \(45%\)\s*(\d+\.?\d*)%/i)
        }
      };
    }

    try {
      const formData = new FormData();
      formData.append('file', selectedFile);
      // Add all frame settings to formData and query params
      Object.entries(frameSettings).forEach(([key, value]) => {
        if (value !== null && value !== undefined) formData.append(key, value);
      });
      console.log('Uploading file:', selectedFile.name);

      // Build query string from frameSettings
      const params = new URLSearchParams();
      Object.entries(frameSettings).forEach(([key, value]) => {
        if (value !== null && value !== undefined) params.append(key, value);
      });

      const response = await fetch(`${API_BASE_URL}/upload-video?${params.toString()}`, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const analysisRaw = await response.text();
      let analysisObj = {};
      try {
        analysisObj = JSON.parse(analysisRaw);
      } catch (e) {
        analysisObj = { full_report: analysisRaw };
      }
      setAnalysis(analysisObj);

      // Use structured JSON if available, else fallback to raw parsing
      if (analysisObj.analysis_json && Object.keys(analysisObj.analysis_json).length > 0) {
        const aj = analysisObj.analysis_json;
        // Map backend JSON to frontend fields
        setParsedReport({
          duration: aj.video_info?.duration || aj.video_info?.duration_formatted || 'N/A',
          resolution: aj.video_info?.resolution || 'N/A',
          fps: aj.video_info?.fps || 'N/A',
          processing_time: aj.video_info?.analysis_time || aj.processing_time || 'N/A',
          text_score: aj.frame_analysis?.average_confidence ? (aj.frame_analysis.average_confidence * 100).toFixed(1) : 'N/A',
          text_status: aj.overall_assessment?.status || 'N/A',
          text_issues: aj.recommendations ? aj.recommendations.join('\n') : '',
          audio_score: aj.overall_assessment?.audio_compliance ? aj.overall_assessment.audio_compliance.toFixed(1) : 'N/A',
          audio_status: aj.overall_assessment?.status || 'N/A',
          audio_issues: aj.audio_analysis?.policy_flags ? Object.keys(aj.audio_analysis.policy_flags).join(', ') : '',
          image_score: aj.overall_assessment?.visual_compliance ? aj.overall_assessment.visual_compliance.toFixed(1) : 'N/A',
          image_status: aj.overall_assessment?.status || 'N/A',
          image_issues: aj.frame_analysis?.violation_categories ? aj.frame_analysis.violation_categories.join(', ') : '',
          frames_analyzed: aj.frame_analysis?.total_frames || 0,
          overall: aj.overall_assessment?.status || 'N/A',
          overall_scores: {
            text: aj.overall_assessment?.visual_compliance ? aj.overall_assessment.visual_compliance.toFixed(1) : 'N/A',
            audio: aj.overall_assessment?.audio_compliance ? aj.overall_assessment.audio_compliance.toFixed(1) : 'N/A',
            image: aj.overall_assessment?.overall_compliance ? aj.overall_assessment.overall_compliance.toFixed(1) : 'N/A'
          }
        });
      } else {
        // Fallback to raw report parsing
        setParsedReport(parseReportFields(analysisObj.full_report));
      }

    } catch (err) {
      console.error('Analysis error:', err);
      setError(`Analysis failed: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'conforme': return 'success';
      case 'attention': return 'warning';
      case 'non_conforme': return 'error';
      default: return 'default';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'conforme': return <CheckCircle />;
      case 'attention': return <Warning />;
      case 'non_conforme': return <ErrorIcon />;
      default: return null;
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2) + ' ' + sizes[i]);
  };

  // Parse RAG analysis from backend report JSON
  useEffect(() => {
    if (analysis && (analysis.rag_explanations || (analysis.analysis_json && analysis.analysis_json.rag_explanations))) {
      // Prefer top-level rag_explanations, fallback to analysis_json
      let ragFrames = analysis.rag_explanations || analysis.analysis_json.rag_explanations;
      // Fallback: if no violation detected, mark as safe_content
      ragFrames = ragFrames.map((frame) => {
        if (!frame.category || frame.category === '' || frame.category === 'N/A') {
          return { ...frame, category: 'safe_content', reasoning: 'No violation detected. Content is safe.' };
        }
        return frame;
      });
      // Limit frames to user-selected frameCount
      if (frameSettings && frameSettings.frameCount && ragFrames.length > frameSettings.frameCount) {
        ragFrames = ragFrames.slice(0, frameSettings.frameCount);
      }
      setRagAnalysis({
        decision: analysis.analysis_json?.overall_assessment?.status || 'N/A',
        confidence: analysis.analysis_json?.frame_analysis?.average_confidence || 'N/A',
        reasoning: 'Detailed frame-level policy violation explanations below.',
        retrieved_docs: ragFrames.map((frame, idx) => ({
          title: frame.type === 'visual'
            ? `Frame ${frame.frame} (t=${frame.timestamp}s): ${frame.category} detected by model (confidence ${frame.confidence})`
            : `Audio: ${frame.category} detected in transcript (severity: ${frame.severity})`,
          category: frame.category,
          confidence: frame.confidence,
          blip: frame.blip,
          ocr: frame.ocr,
          policy: frame.policy,
          examples: frame.examples,
          action_required: frame.action_required,
          severity_indicators: frame.severity_indicators,
          context_factors: frame.context_factors,
          reasoning: frame.reasoning,
          transcript: frame.transcript,
          keywords: frame.keywords
        }))
      });
    } else if (analysis && analysis.full_report) {
      // Fallback: parse DETAILED POLICY VIOLATION EXPLANATIONS (RAG) from raw report text
      const ragSectionMatch = analysis.full_report.match(/DETAILED POLICY VIOLATION EXPLANATIONS \(RAG\):([\s\S]*?)(?=\n\s*\n|$)/i);
      if (ragSectionMatch) {
        const ragText = ragSectionMatch[1];
        // Split by frame
        const frameRegex = /- Frame (\d+) \(t=([\d\.]+)s\): ([^\n]+)\n([\s\S]*?)(?=(?:- Frame \d+ \(t=|$))/g;
        let frames = [];
        let match;
        while ((match = frameRegex.exec(ragText)) !== null) {
          const frameIdx = match[1];
          const timestamp = match[2];
          const category = match[3] && match[3] !== '' ? match[3] : 'safe_content';
          const details = match[4];
          frames.push({
            title: `Frame ${frameIdx} (t=${timestamp}s): ${category}`,
            category,
            confidence: undefined,
            blip: undefined,
            ocr: undefined,
            policy: undefined,
            examples: undefined,
            action_required: undefined,
            severity_indicators: undefined,
            context_factors: undefined,
            reasoning: category === 'safe_content' ? 'No violation detected. Content is safe.' : undefined,
            transcript: undefined,
            keywords: undefined
          });
        }
        // Limit frames to user-selected frameCount
        if (frameSettings && frameSettings.frameCount && frames.length > frameSettings.frameCount) {
          frames = frames.slice(0, frameSettings.frameCount);
        }
        setRagAnalysis({
          decision: parsedReport?.overall || 'N/A',
          confidence: parsedReport?.image_score || 'N/A',
          reasoning: 'Detailed frame-level policy violation explanations below.',
          retrieved_docs: frames
        });
      } else {
        setRagAnalysis(null);
      }
    }
  }, [analysis, parsedReport]);

  // Count safe_content frames for summary
  const safeCount = ragAnalysis && ragAnalysis.retrieved_docs
    ? ragAnalysis.retrieved_docs.filter(doc => doc.category === 'safe_content').length
    : 0;
  const totalFrames = ragAnalysis && ragAnalysis.retrieved_docs ? ragAnalysis.retrieved_docs.length : 0;

  const [showUploadHistoryPage, setShowUploadHistoryPage] = useState(false);

  if (showRagPage) {
    return (
      <>
        <Box sx={{ p: 3, mb: 2 }}>
          <Typography variant="h5" color="success.main">
            Safe Content Frames: {safeCount} / {totalFrames}
          </Typography>
          <LinearProgress variant="determinate" value={totalFrames ? (safeCount / totalFrames) * 100 : 0} sx={{ height: 10, borderRadius: 5, mt: 1 }} color="success" />
        </Box>
        <RagAnalysisPage ragAnalysis={ragAnalysis} onBack={() => setShowRagPage(false)} />
      </>
    );
  }

  if (showSettings) {
    return <SettingsPage settings={frameSettings} onSave={settings => { setFrameSettings(settings); setShowSettings(false); }} />;
  }

  // Sidebar navigation actions
  const sidebarOptions = [
    { label: 'SETTINGS', color: 'secondary', onClick: () => setShowSettings(true) },
    { label: 'RAG ANALYSIS', color: 'info', onClick: () => setShowRagPage(true) },
    { label: 'UPLOAD HISTORY', color: 'primary', onClick: () => setShowUploadHistoryPage(true) },
    { label: 'CUSTOMIZE', color: 'warning', onClick: () => alert('Customize feature coming soon!') },
  ];

  // Main render
  return (
    <Box sx={{ display: 'flex', minHeight: '100vh', backgroundColor: '#f5f5f5' }}>
      {/* Sidebar */}
      <Box sx={{ width: 220, background: 'linear-gradient(180deg,#1976d2 0%,#21cbf3 100%)', color: '#fff', display: { xs: 'none', md: 'flex' }, flexDirection: 'column', alignItems: 'center', py: 4, boxShadow: 3 }}>
        <Box sx={{ mb: 3 }}>
          <img src="https://github.githubassets.com/images/modules/logos_page/GitHub-Mark.png" alt="Menu" style={{ width: 56, borderRadius: '50%' }} />
        </Box>
        <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 2, letterSpacing: 1 }}>
          Menu
        </Typography>
        {sidebarOptions.map(opt => (
          <Button key={opt.label} variant="contained" color={opt.color} sx={{ mb: 2, width: '90%', fontWeight: 'bold', fontSize: '1.1rem', borderRadius: 3, boxShadow: 2, textTransform: 'none' }} onClick={opt.onClick}>
            {opt.label}
          </Button>
        ))}
      </Box>
      {/* Main content */}
      <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column', minHeight: '100vh', background: '#f5f5f5' }}>
        <AppBar position="static" sx={{ background: 'linear-gradient(90deg,#1976d2 60%,#21cbf3 100%)', boxShadow: 2 }}>
          <Toolbar>
            <Typography variant="h4" sx={{ fontWeight: 'bold', flexGrow: 1, letterSpacing: 1, color: '#fff' }}>
              AI Video Content Analyzer
            </Typography>
          </Toolbar>
        </AppBar>
        <Container maxWidth="xl" sx={{ mt: 4, pb: 4 }}>
          <Grid container spacing={4} alignItems="stretch" justifyContent="center">
            {/* Welcome/Info Section */}
            <Grid item xs={12} md={7} lg={7}>
              <Box sx={{
                minHeight: 320,
                width: '100%',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                background: 'linear-gradient(120deg, #1976d2 60%, #21cbf3 100%)',
                borderRadius: 6,
                boxShadow: '0 8px 32px rgba(33,203,243,0.15)',
                px: 6,
                py: 6,
                mt: 2,
              }}>
                <Box display="flex" alignItems="center" justifyContent="center" mb={2}>
                  <VideoLibrary sx={{ fontSize: 72, mr: 2, color: '#fff' }} />
                  <Analytics sx={{ fontSize: 56, mr: 2, color: '#fffde7' }} />
                  <CheckCircle sx={{ fontSize: 56, color: '#e8f5e9' }} />
                </Box>
                <Typography variant="h2" sx={{ fontWeight: 'bold', color: '#fff', mb: 2, textShadow: '1px 1px 8px #1976d2', letterSpacing: 1 }}>
                  Welcome to AI Video Content Analyzer
                </Typography>
                <Typography variant="h5" sx={{ color: '#e3f2fd', mb: 3, fontWeight: 500 }}>
                  Analyze your videos for YouTube policy compliance in seconds!
                </Typography>
                <Typography variant="body1" sx={{ color: '#fff', mb: 3, maxWidth: 600, textAlign: 'center', fontSize: '1.15rem' }}>
                  <b>How it works:</b> Upload a video file and get instant feedback on compliance, violations, and recommendations.<br />
                  Powered by advanced AI models for text, audio, and image analysis.
                </Typography>
                <Typography variant="body2" sx={{ color: '#fffde7', mb: 4, fontSize: '1.1rem' }}>
                  <b>Supported formats:</b> MP4, AVI, MOV, MKV
                </Typography>
                <Button
                  variant="contained"
                  color="secondary"
                  size="large"
                  sx={{ fontWeight: 'bold', px: 6, py: 2, fontSize: '1.3rem', boxShadow: 3, borderRadius: 3, background: 'linear-gradient(90deg,#8e24aa 60%,#d1c4e9 100%)', ':hover': { background: 'linear-gradient(90deg,#6a1b9a 60%,#b39ddb 100%)', transform: 'scale(1.05)' } }}
                  onClick={() => document.getElementById('video-upload').click()}
                >
                  <CloudUpload sx={{ mr: 2, fontSize: 32 }} /> START ANALYSIS
                </Button>
              </Box>
            </Grid>
            {/* Upload Section */}
            <Grid item xs={12} md={5} lg={5}>
              <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', width: '100%' }}>
                <Card sx={{ width: '100%', maxWidth: 420, mx: 'auto', p: 4, boxShadow: '0 4px 24px rgba(33,203,243,0.12)', borderRadius: 5, background: '#fff' }}>
                  <CardContent>
                    <Typography variant="h5" align="center" sx={{ mb: 3, fontWeight: 'bold', color: '#1976d2', letterSpacing: 1 }}>
                      Full Video Moderation (Upload)
                    </Typography>
                    {/* Display chosen frame settings */}
                    <Box sx={{ mb: 2, p: 2, background: '#e3f2fd', borderRadius: 2, fontSize: '1rem', color: '#1976d2' }}>
                      <Typography variant="subtitle2" sx={{ fontWeight: 'bold', mb: 1 }}>Current Frame Extraction Settings:</Typography>
                      <ul style={{ margin: 0, paddingLeft: 20 }}>
                        <li>Frames: <b>{frameSettings.frameCount}</b></li>
                        <li>Interval: <b>{frameSettings.frameInterval}</b> sec</li>
                        <li>Resolution: <b>{frameSettings.frameResolution}</b></li>
                        <li>Format: <b>{frameSettings.frameFormat}</b></li>
                        <li>Start: <b>{frameSettings.frameStart}</b> sec</li>
                        <li>End: <b>{frameSettings.frameEnd !== null ? frameSettings.frameEnd : 'Full video'}</b></li>
                        <li>Sampling: <b>{frameSettings.frameSampling}</b></li>
                      </ul>
                    </Box>
                    <Box
                      sx={{
                        border: '2px dashed #1976d2',
                        borderRadius: 3,
                        p: 3,
                        mb: 4,
                        textAlign: 'center',
                        background: '#e3f2fd',
                        cursor: 'pointer',
                        transition: '0.2s',
                        ':hover': { background: '#bbdefb', borderColor: '#1565c0' },
                      }}
                      onClick={() => document.getElementById('video-upload').click()}
                      onDragOver={e => e.preventDefault()}
                      onDrop={e => {
                        e.preventDefault();
                        if (e.dataTransfer.files && e.dataTransfer.files[0]) {
                          handleFileChange({ target: { files: e.dataTransfer.files } });
                        }
                      }}
                    >
                      <input
                        accept="video/*"
                        style={{ display: 'none' }}
                        id="video-upload"
                        type="file"
                        onChange={handleFileChange}
                      />
                      {selectedFile ? (
                        <Typography variant="body1" color="primary" sx={{ fontWeight: 'bold', fontSize: '1.1rem' }}>
                          {selectedFile.name} ({formatFileSize(selectedFile.size)})
                        </Typography>
                      ) : (
                        <Typography variant="body1" color="text.secondary" sx={{ fontSize: '1.1rem' }}>
                          Drag & Drop a video here, or click to upload
                        </Typography>
                      )}
                    </Box>
                    <Button
                      variant="contained"
                      color="primary"
                      fullWidth
                      sx={{ fontWeight: 'bold', py: 2, fontSize: '1.15rem', borderRadius: 3, boxShadow: 2, background: 'linear-gradient(90deg,#1976d2 60%,#21cbf3 100%)', ':hover': { background: 'linear-gradient(90deg,#1565c0 60%,#039be5 100%)', transform: 'scale(1.03)' } }}
                      onClick={handleAnalyze}
                      disabled={loading || !selectedFile || connectionStatus !== 'connected'}
                    >
                      {loading ? 'Uploading...' : 'UPLOAD VIDEO'}
                    </Button>
                    {error && (
                      <Alert severity="error" sx={{ mt: 3 }}>
                        {error}
                      </Alert>
                    )}
                  </CardContent>
                </Card>
                {/* Progress Bar with Percentage */}
                {loading && (
                  <Paper sx={{ p: 2, mb: 4, width: '100%' }}>
                    <Typography variant="h6" gutterBottom>
                      Analyzing video content...
                    </Typography>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                      <Box sx={{ width: '100%', mr: 2 }}>
                        <LinearProgress variant="determinate" value={progress} />
                      </Box>
                      <Typography variant="body2" sx={{ minWidth: 40 }}>{progress}%</Typography>
                    </Box>
                  </Paper>
                )}
                {/* Redesigned Model Report Section */}
                {parsedReport && (
                  <Box sx={{ mb: 4 }}>
                    {/* General Summary Card */}
                    <Card sx={{ mb: 3, background: 'linear-gradient(90deg,#1976d2 60%,#21cbf3 100%)', color: '#fff', boxShadow: 3, borderRadius: 4 }}>
                      <CardContent>
                        <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 2 }}>
                          <Analytics sx={{ mr: 1 }} /> General Compliance Report
                        </Typography>
                        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 3 }}>
                          <Box>
                            <Typography variant="subtitle1"><b>Status:</b> {parsedReport.overall}</Typography>
                            <Typography variant="subtitle1"><b>Compliance:</b> {parsedReport.image_score}%</Typography>
                            <Typography variant="subtitle1"><b>Frames Analyzed:</b> {parsedReport.frames_analyzed}</Typography>
                          </Box>
                          <Box>
                            <Typography variant="subtitle1"><b>Violations:</b> {parsedReport.image_issues || 'None'}</Typography>
                            <Typography variant="subtitle1"><b>Audio Issues:</b> {parsedReport.audio_issues || 'None'}</Typography>
                          </Box>
                        </Box>
                        <Box sx={{ mt: 2 }}>
                          <Typography variant="subtitle1"><b>Recommendations:</b></Typography>
                          <Typography variant="body2">{parsedReport.text_issues || 'No recommendations.'}</Typography>
                        </Box>
                      </CardContent>
                    </Card>

                    {/* Audio Section */}
                    <Card sx={{ mb: 3, background: '#e3f2fd', boxShadow: 2, borderRadius: 4 }}>
                      <CardContent>
                        <Typography variant="h6" color="primary" sx={{ fontWeight: 'bold', mb: 2 }}>
                          <Warning sx={{ mr: 1, color: '#1976d2' }} /> Audio Analysis
                        </Typography>
                        <Typography variant="subtitle2"><b>Audio Issues:</b> {parsedReport.audio_issues || 'None'}</Typography>
                        <Typography variant="subtitle2"><b>Audio Score:</b> {parsedReport.audio_score || 'N/A'}%</Typography>
                        <Typography variant="subtitle2"><b>Status:</b> {parsedReport.audio_status || 'N/A'}</Typography>
                        {ragAnalysis && ragAnalysis.retrieved_docs && ragAnalysis.retrieved_docs.filter(doc => doc.title.startsWith('Audio')).length > 0 && (
                          <Box sx={{ mt: 2 }}>
                            <Typography variant="subtitle2" color="secondary">Audio Details:</Typography>
                            {ragAnalysis.retrieved_docs.filter(doc => doc.title.startsWith('Audio')).map((doc, idx) => (
                              <Box key={idx} sx={{ mb: 2, p: 2, background: '#fff', borderRadius: 2, boxShadow: 1 }}>
                                <Typography variant="subtitle2" color="primary"><b>{doc.title}</b></Typography>
                                {doc.transcript && <Typography variant="body2"><b>Transcript:</b> {doc.transcript}</Typography>}
                                {doc.keywords && doc.keywords.length > 0 && <Typography variant="body2"><b>Keywords:</b> {doc.keywords.join(', ')}</Typography>}
                                {doc.policy && <Typography variant="body2"><b>Policy:</b> {doc.policy.description}</Typography>}
                                {doc.action_required && <Typography variant="body2"><b>Action Required:</b> {doc.action_required}</Typography>}
                              </Box>
                            ))}
                          </Box>
                        )}
                      </CardContent>
                    </Card>

                    {/* Frames Section */}
                    <Card sx={{ mb: 3, background: '#f9fbe7', boxShadow: 2, borderRadius: 4 }}>
                      <CardContent>
                        <Typography variant="h6" color="success.main" sx={{ fontWeight: 'bold', mb: 2 }}>
                          <VideoLibrary sx={{ mr: 1, color: 'success.main' }} /> Frame Analysis
                        </Typography>
                        <Typography variant="subtitle2"><b>Frames Analyzed:</b> {parsedReport.frames_analyzed}</Typography>
                        <Typography variant="subtitle2"><b>Violations:</b> {parsedReport.image_issues || 'None'}</Typography>
                        <Typography variant="subtitle2"><b>Frame Score:</b> {parsedReport.image_score || 'N/A'}%</Typography>
                        <Typography variant="subtitle2"><b>Status:</b> {parsedReport.image_status || 'N/A'}</Typography>
                        {ragAnalysis && ragAnalysis.retrieved_docs && ragAnalysis.retrieved_docs.filter(doc => doc.title.startsWith('Frame')).length > 0 && (
                          <Box sx={{ mt: 2 }}>
                            <Typography variant="subtitle2" color="secondary">Frame Details:</Typography>
                            {ragAnalysis.retrieved_docs.filter(doc => doc.title.startsWith('Frame')).map((doc, idx) => (
                              <Box key={idx} sx={{ mb: 2, p: 2, background: '#fff', borderRadius: 2, boxShadow: 1 }}>
                                <Typography variant="subtitle2" color="primary"><b>{doc.title}</b></Typography>
                                {doc.reasoning && <Typography variant="body2">{doc.reasoning}</Typography>}
                                {doc.blip && <Typography variant="body2"><b>BLIP:</b> {doc.blip}</Typography>}
                                {doc.ocr && <Typography variant="body2"><b>OCR:</b> {doc.ocr}</Typography>}
                                {doc.policy && <Typography variant="body2"><b>Policy:</b> {doc.policy.description}</Typography>}
                                {doc.examples && doc.examples.length > 0 && <Typography variant="body2"><b>Examples:</b> {doc.examples.join(', ')}</Typography>}
                                {doc.action_required && <Typography variant="body2"><b>Action Required:</b> {doc.action_required}</Typography>}
                                {doc.severity_indicators && doc.severity_indicators.length > 0 && <Typography variant="body2"><b>Severity Indicators:</b> {doc.severity_indicators.join(', ')}</Typography>}
                                {doc.context_factors && doc.context_factors.length > 0 && <Typography variant="body2"><b>Context Factors:</b> {doc.context_factors.join(', ')}</Typography>}
                              </Box>
                            ))}
                          </Box>
                        )}
                      </CardContent>
                    </Card>

                    {/* Extra Details Section */}
                    <Card sx={{ mb: 3, background: '#fffde7', boxShadow: 1, borderRadius: 4 }}>
                      <CardContent>
                        <Typography variant="h6" color="secondary" sx={{ fontWeight: 'bold', mb: 2 }}>
                          <ErrorIcon sx={{ mr: 1, color: '#d32f2f' }} /> Extra Details
                        </Typography>
                        <Typography variant="body2" sx={{ color: '#333' }}>
                          <b>Duration:</b> {parsedReport.duration || 'N/A'}<br />
                          <b>Resolution:</b> {parsedReport.resolution || 'N/A'}<br />
                          <b>FPS:</b> {parsedReport.fps || 'N/A'}<br />
                          <b>Processing Time:</b> {parsedReport.processing_time || 'N/A'}<br />
                        </Typography>
                      </CardContent>
                    </Card>
                  </Box>
                )}
              </Box>
            </Grid>
          </Grid>
          {/* History removed from main dashboard. Now available as a separate page. */}
          {connectionStatus === 'disconnected' && (
            <Alert severity="error" sx={{ mb: 4 }} action={
              <Button color="inherit" size="small" onClick={testConnection}>
                Retry
              </Button>
            }>
              Cannot connect to backend server. Make sure the server is running on http://localhost:8000
            </Alert>
          )}
          {connectionStatus === 'connected' && (
            <Alert severity="success" sx={{ mb: 4 }}>
              âœ… AI Video Analyzer ready! Upload and analyze your video files.
            </Alert>
          )}
        </Container>
      </Box>
    </Box>
  );
}

export default App;
