import React, { useState } from 'react';
import { Card, CardContent, Typography, Box, Chip, Divider } from '@mui/material';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';
import InfoIcon from '@mui/icons-material/Info';
import AudiotrackIcon from '@mui/icons-material/Audiotrack';
import TextFieldsIcon from '@mui/icons-material/TextFields';
import ImageIcon from '@mui/icons-material/Image';
import CopyrightIcon from '@mui/icons-material/Copyright';
import DownloadIcon from '@mui/icons-material/Download';

function ComprehensiveReportDisplay({ report }) {
    if (!report) return null;

    // Declare all variables only once
    const parsed = report.analysis_json ? report : null;
    const fields = parsed && parsed.analysis_json ? parsed.analysis_json : {};
    const fallback = report.parsedReport || {};
    const violationCount = fields.frame_analysis?.violation_categories?.length || 0;
    const safeFrames = fields.frame_analysis?.safe_frames || 0;
    const totalFrames = fields.frame_analysis?.total_frames || fallback.frames_analyzed || 0;
    const complianceRate = totalFrames ? Math.round((safeFrames / totalFrames) * 100) : 0;
    const transcript = fields.audio_analysis?.transcript || fields.transcript || fallback.transcript || '';
    const ocrText = fields.ocr_results?.text || fields.text_in_video?.text || fallback.ocr_text || '';
    const ocrItems = fields.ocr_results?.extracted_items || fields.text_in_video?.extracted_items || fallback.ocr_items || [];
    const blipDescriptions = fields.frame_analysis?.blip_descriptions || fallback.blip_descriptions || [];
    const copyrightCheck = report.copyright_check || null;

    // Helper: check for bad words in transcript
    const BAD_WORDS = [
        'badword1', 'badword2', 'hate', 'violence', 'explicit', 'offensive', 'racist', 'sexist', 'drugs', 'weapon', 'kill', 'shoot', 'bomb', 'terror', 'abuse', 'adult', 'nsfw', 'profanity', 'curse', 'swear', 'misinformation', 'suggestive', 'inappropriate'
    ];
    const transcriptLower = transcript ? transcript.toLowerCase() : '';
    const foundBadWords = transcript ? BAD_WORDS.filter(word => transcriptLower.includes(word)) : [];

    // Collapsible section helper
    const CollapsibleSection = ({ title, icon, children, defaultOpen = true }) => {
        const [open, setOpen] = useState(defaultOpen);
        return (
            <Box sx={{ mb: 2 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }} onClick={() => setOpen(!open)}>
                    {icon}
                    <Typography variant="h6" sx={{ fontWeight: 'bold', ml: 1 }}>{title}</Typography>
                    <Box sx={{ ml: 2, color: open ? 'primary.main' : 'grey.500', fontWeight: 'bold' }}>{open ? '▼' : '▲'}</Box>
                </Box>
                {open && <Box sx={{ mt: 1 }}>{children}</Box>}
                <Divider sx={{ my: 1 }} />
            </Box>
        );
    };

    // Status badge helper
    const StatusBadge = ({ status }) => {
        let color = 'success';
        let icon = <CheckCircleIcon fontSize="small" sx={{ verticalAlign: 'middle', mr: 0.5 }} />;
        if (status === 'major_violations' || status === 'non_conforme') {
            color = 'error';
            icon = <ErrorIcon fontSize="small" sx={{ verticalAlign: 'middle', mr: 0.5 }} />;
        } else if (status === 'attention' || status === 'MINOR_VIOLATIONS') {
            color = 'warning';
            icon = <InfoIcon fontSize="small" sx={{ verticalAlign: 'middle', mr: 0.5 }} />;
        }
        return <Chip label={status} color={color} icon={icon} size="small" sx={{ fontWeight: 'bold', ml: 1 }} />;
    };

    // Remove duplicate declarations below

    // Extract RAG explanations and relevant policies if present
    const ragExplanations = report.rag_explanations || [];
    const relevantPolicies = report.analysis_json?.relevant_policies || [];
    // Enhanced RAG details: frame-level explanations, matched examples, and reasoning
    const frameLevelRag = report.analysis_json?.rag_explanations || ragExplanations || [];

    // Helper to display a section
    const Section = ({ title, children }) => (
        <Box sx={{ mb: 2 }}>
            <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 1 }}>{title}</Typography>
            {children}
            <Divider sx={{ my: 1 }} />
        </Box>
    );

    return (
        <Box sx={{ mt: 4, display: 'flex', justifyContent: 'center' }}>
            <Card sx={{ boxShadow: 6, borderRadius: 4, width: '100%', maxWidth: 1200, p: 2, background: 'linear-gradient(90deg,#e3f2fd 0%,#fafcff 100%)' }}>
                <CardContent>
                    {/* Source badge */}
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                        <Chip label="Source: Model Output" color="primary" sx={{ fontWeight: 'bold', fontSize: 16, px: 2, py: 1, mr: 2 }} />
                        {report.analysis_json && <Chip label="Analysis: AI Model" color="success" sx={{ fontWeight: 'bold', fontSize: 16, px: 2, py: 1 }} />}
                    </Box>
                    {/* Summary widgets */}
                    <Box sx={{ display: 'flex', gap: 3, mb: 3, flexWrap: 'wrap', justifyContent: 'center' }}>
                        <Box sx={{ background: '#e8f5e9', borderRadius: 2, boxShadow: 1, px: 3, py: 2, minWidth: 160, textAlign: 'center' }}>
                            <CheckCircleIcon color="success" sx={{ fontSize: 32 }} />
                            <Typography variant="subtitle2" sx={{ fontWeight: 'bold', color: 'success.main' }}>Compliance Rate</Typography>
                            <Typography variant="h6" sx={{ fontWeight: 'bold', color: 'success.main' }}>{complianceRate}%</Typography>
                        </Box>
                        <Box sx={{ background: '#fffde7', borderRadius: 2, boxShadow: 1, px: 3, py: 2, minWidth: 160, textAlign: 'center' }}>
                            <ErrorIcon color="warning" sx={{ fontSize: 32 }} />
                            <Typography variant="subtitle2" sx={{ fontWeight: 'bold', color: 'warning.main' }}>Violations</Typography>
                            <Typography variant="h6" sx={{ fontWeight: 'bold', color: 'warning.main' }}>{violationCount}</Typography>
                        </Box>
                        <Box sx={{ background: '#e3f2fd', borderRadius: 2, boxShadow: 1, px: 3, py: 2, minWidth: 160, textAlign: 'center' }}>
                            <InfoIcon color="info" sx={{ fontSize: 32 }} />
                            <Typography variant="subtitle2" sx={{ fontWeight: 'bold', color: 'info.main' }}>Frames Analyzed</Typography>
                            <Typography variant="h6" sx={{ fontWeight: 'bold', color: 'info.main' }}>{totalFrames}</Typography>
                        </Box>
                        <Box sx={{ background: '#f5faff', borderRadius: 2, boxShadow: 1, px: 3, py: 2, minWidth: 160, textAlign: 'center', cursor: 'pointer' }}>
                            <DownloadIcon color="primary" sx={{ fontSize: 32 }} />
                            <Typography variant="subtitle2" sx={{ fontWeight: 'bold', color: 'primary.main' }}>Download Report</Typography>
                            <Typography variant="body2" sx={{ color: 'primary.main', fontWeight: 500 }}>PDF/JSON</Typography>
                        </Box>
                    </Box>
                    <Box sx={{ display: 'flex', flexDirection: { xs: 'column', md: 'row' }, gap: 4, alignItems: 'flex-start' }}>
                        {/* Left column: Extracted Content */}
                        <Box sx={{ flex: 1, minWidth: 320, background: '#f5faff', borderRadius: 3, boxShadow: 2, p: 2 }}>
                            {/* Transcript Safety Section (always shown) */}
                            <CollapsibleSection title="Transcript Safety (Model Output)" icon={<AudiotrackIcon color="info" sx={{ fontSize: 28, mr: 1 }} />} defaultOpen={true}>
                                {transcript === '' ? (
                                    <Chip label="No transcript available" color="warning" sx={{ fontWeight: 'bold', fontSize: 15, mb: 1 }} />
                                ) : foundBadWords.length === 0 ? (
                                    <Chip label="Safe: No bad words detected" color="success" sx={{ fontWeight: 'bold', fontSize: 15, mb: 1 }} />
                                ) : (
                                    <Chip label={`Flagged: Bad words detected (${foundBadWords.join(', ')})`} color="error" sx={{ fontWeight: 'bold', fontSize: 15, mb: 1 }} />
                                )}
                            </CollapsibleSection>
                            {(transcript || ocrText || ocrItems.length > 0 || blipDescriptions.length > 0) && (
                                <CollapsibleSection title="Extracted Content (Model Output)" icon={<TextFieldsIcon color="primary" sx={{ fontSize: 28, mr: 1 }} />} defaultOpen={true}>
                                    {transcript && (
                                        <Typography variant="body2" sx={{ mb: 1 }}>
                                            <AudiotrackIcon color="info" sx={{ fontSize: 18, mr: 1 }} /> <b>Audio Transcript:</b> {transcript}
                                        </Typography>
                                    )}
                                    {ocrText && (
                                        <Typography variant="body2" sx={{ mb: 1 }}>
                                            <TextFieldsIcon color="secondary" sx={{ fontSize: 18, mr: 1 }} /> <b>OCR Text:</b> {ocrText}
                                        </Typography>
                                    )}
                                    {ocrItems.length > 0 && (
                                        <Box sx={{ mb: 1 }}>
                                            <Typography variant="body2" sx={{ fontWeight: 'bold', mb: 0.5 }} color="secondary">
                                                OCR Extracted Items:
                                            </Typography>
                                            {ocrItems.map((item, idx) => (
                                                <Typography key={idx} variant="body2" sx={{ ml: 2, mb: 0.5, fontStyle: 'italic' }}>
                                                    • {item}
                                                </Typography>
                                            ))}
                                        </Box>
                                    )}
                                    {blipDescriptions.length > 0 && (
                                        <Box sx={{ mb: 1 }}>
                                            <Typography variant="body2" sx={{ fontWeight: 'bold', mb: 0.5 }} color="secondary">
                                                BLIP Frame Descriptions:
                                            </Typography>
                                            {blipDescriptions.map((desc, idx) => (
                                                <Typography key={idx} variant="body2" sx={{ ml: 2, mb: 0.5, fontStyle: 'italic' }}>
                                                    • {desc}
                                                </Typography>
                                            ))}
                                        </Box>
                                    )}
                                </CollapsibleSection>
                            )}
                        </Box>
                        {/* Right column: Model Analysis Report */}
                        <Box sx={{ flex: 2, minWidth: 340, background: '#f8f8ff', borderRadius: 3, boxShadow: 2, p: 2 }}>
                            <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 2, color: '#1976d2' }}>
                                Model Analysis Report
                            </Typography>
                            <CollapsibleSection title="General Info (Model Output)" icon={<InfoIcon color="primary" sx={{ fontSize: 24, mr: 1 }} />} defaultOpen={true}>
                                <Typography variant="body2">⏱ <b>Duration:</b> {fields.video_info?.duration || fallback.duration || 'N/A'}</Typography>
                                <Typography variant="body2">🖥 <b>Resolution:</b> {fields.video_info?.resolution || fallback.resolution || 'N/A'}</Typography>
                                <Typography variant="body2">🎞 <b>FPS:</b> {fields.video_info?.fps || fallback.fps || 'N/A'}</Typography>
                                <Typography variant="body2">⏳ <b>Processing Time:</b> {fields.video_info?.analysis_time || fallback.processing_time || 'N/A'}</Typography>
                            </CollapsibleSection>

                            {/* Enhanced RAG policy reasoning for violations, including explicit examples */}
                            {relevantPolicies.length > 0 && (
                                <CollapsibleSection title="YouTube Policy Violation Reasoning" icon={<ErrorIcon color="error" sx={{ fontSize: 24, mr: 1 }} />} defaultOpen={false}>
                                    {relevantPolicies.map((policy, idx) => (
                                        <Box key={idx} sx={{ mb: 2, p: 2, background: '#fffde7', borderRadius: 2 }}>
                                            <Typography variant="subtitle2" sx={{ fontWeight: 'bold', color: '#d32f2f' }}>
                                                {policy.category}
                                            </Typography>
                                            <Typography variant="body2" sx={{ mb: 1 }}>
                                                {policy.policy_info?.description}
                                            </Typography>
                                            <Typography variant="caption" color="text.secondary">
                                                Similarity Score: {(policy.similarity * 100).toFixed(1)}%
                                            </Typography>
                                            {policy.source === 'scraped' && (
                                                <Chip label="Scraped from YouTube" color="info" size="small" sx={{ ml: 1 }} />
                                            )}
                                            {/* Show explicit violation examples if available */}
                                            {Array.isArray(policy.policy_info?.examples) && policy.policy_info.examples.length > 0 && (
                                                <Box sx={{ mt: 1 }}>
                                                    <Typography variant="body2" sx={{ fontWeight: 'bold', mb: 0.5 }} color="secondary">
                                                        Explicit Violation Examples:
                                                    </Typography>
                                                    {policy.policy_info.examples.map((ex, exIdx) => (
                                                        <Typography key={exIdx} variant="body2" sx={{ ml: 2, mb: 0.5, fontStyle: 'italic' }}>
                                                            • {ex}
                                                        </Typography>
                                                    ))}
                                                </Box>
                                            )}
                                        </Box>
                                    ))}
                                </CollapsibleSection>
                            )}
                            <CollapsibleSection title="Text Analysis (Model Output)" icon={<TextFieldsIcon color="primary" sx={{ fontSize: 24, mr: 1 }} />} defaultOpen={true}>
                                <Typography variant="body2">📝 <b>Score:</b> {fields.frame_analysis?.average_confidence ? (fields.frame_analysis.average_confidence * 100).toFixed(1) : fallback.text_score || 'N/A'}%</Typography>
                                <Typography variant="body2">🔎 <b>Status:</b> <StatusBadge status={fields.overall_assessment?.status || fallback.text_status || 'N/A'} /></Typography>
                                <Typography variant="body2">⚠️ <b>Issues:</b> {fields.recommendations ? fields.recommendations.join(', ') : (fallback.text_issues || 'None')}</Typography>
                            </CollapsibleSection>
                            <CollapsibleSection title="Audio Analysis (Model Output)" icon={<AudiotrackIcon color="info" sx={{ fontSize: 24, mr: 1 }} />} defaultOpen={false}>
                                <Typography variant="body2">🔊 <b>Score:</b> {fields.overall_assessment?.audio_compliance ? fields.overall_assessment.audio_compliance.toFixed(1) : fallback.audio_score || 'N/A'}%</Typography>
                                <Typography variant="body2">🔎 <b>Status:</b> {
                                    fields.audio_analysis?.policy_flags?.safe
                                        ? <StatusBadge status="safe" />
                                        : <StatusBadge status={fields.overall_assessment?.status || fallback.audio_status || 'N/A'} />
                                }</Typography>
                                <Typography variant="body2">⚠️ <b>Issues:</b> {
                                    fields.audio_analysis?.policy_flags?.safe
                                        ? 'No bad words or violations detected.'
                                        : (fields.audio_analysis?.policy_flags ? Object.keys(fields.audio_analysis.policy_flags).join(', ') : (fallback.audio_issues || 'None'))
                                }</Typography>
                            </CollapsibleSection>
                            <CollapsibleSection title="Frame/Image Analysis (Model Output)" icon={<ImageIcon color="secondary" sx={{ fontSize: 24, mr: 1 }} />} defaultOpen={false}>
                                <Typography variant="body2">🖼 <b>Score:</b> {fields.overall_assessment?.visual_compliance ? fields.overall_assessment.visual_compliance.toFixed(1) : fallback.image_score || 'N/A'}%</Typography>
                                <Typography variant="body2">🔎 <b>Status:</b> <StatusBadge status={fields.overall_assessment?.status || fallback.image_status || 'N/A'} /></Typography>
                                <Typography variant="body2">⚠️ <b>Issues:</b> {fields.frame_analysis?.violation_categories ? fields.frame_analysis.violation_categories.join(', ') : (fallback.image_issues || 'None')}</Typography>
                                <Typography variant="body2">🖼 <b>Frames Analyzed:</b> {fields.frame_analysis?.total_frames || fallback.frames_analyzed || 'N/A'}</Typography>
                            </CollapsibleSection>
                            {/* Copyright Analysis Section */}
                            {copyrightCheck && (
                                <CollapsibleSection title="Copyright Analysis" icon={<CopyrightIcon color="primary" sx={{ fontSize: 24, mr: 1 }} />} defaultOpen={false}>
                                    {copyrightCheck.violation ? (
                                        <Typography variant="body2" color="error" sx={{ fontWeight: 'bold', mb: 1 }}>
                                            Copyright Violation Detected!
                                        </Typography>
                                    ) : (
                                        <Typography variant="body2" color="success.main" sx={{ fontWeight: 'bold', mb: 1 }}>
                                            No Copyright Violation Detected.
                                        </Typography>
                                    )}
                                    {copyrightCheck.result && (
                                        <Box sx={{ mt: 1 }}>
                                            <Typography variant="body2" sx={{ fontWeight: 'bold', mb: 0.5 }} color="secondary">
                                                AudD API Result:
                                            </Typography>
                                            <Typography variant="body2" sx={{ ml: 2, mb: 0.5, fontStyle: 'italic' }}>
                                                {typeof copyrightCheck.result === 'string' ? copyrightCheck.result : JSON.stringify(copyrightCheck.result, null, 2)}
                                            </Typography>
                                        </Box>
                                    )}
                                </CollapsibleSection>
                            )}
                            <CollapsibleSection title="Overall Compliance (Model Output)" icon={<CheckCircleIcon color="success" sx={{ fontSize: 24, mr: 1 }} />} defaultOpen={true}>
                                <Typography variant="body2">✅ <b>Status:</b> <StatusBadge status={fields.overall_assessment?.status || fallback.overall || 'N/A'} /></Typography>
                                <Typography variant="body2">📝 <b>Text Score:</b> {fields.overall_assessment?.visual_compliance ? fields.overall_assessment.visual_compliance.toFixed(1) : fallback.overall_scores?.text || 'N/A'}%</Typography>
                                <Typography variant="body2">🔊 <b>Audio Score:</b> {fields.overall_assessment?.audio_compliance ? fields.overall_assessment.audio_compliance.toFixed(1) : fallback.overall_scores?.audio || 'N/A'}%</Typography>
                                <Typography variant="body2">🖼 <b>Image Score:</b> {fields.overall_assessment?.overall_compliance ? fields.overall_assessment.overall_compliance.toFixed(1) : fallback.overall_scores?.image || 'N/A'}%</Typography>
                            </CollapsibleSection>
                        </Box>
                    </Box>
                </CardContent>
            </Card>
        </Box>
    );
}

export default ComprehensiveReportDisplay;
