import React, { useState } from 'react';
import ComprehensiveReportDisplay from './ComprehensiveReportDisplay';

function App() {
  const [analysis, setAnalysis] = useState(null);

  // Always show the dashboard full screen with the report
  return (
    <div style={{ width: '100vw', height: '100vh', margin: 0, padding: 0, overflow: 'hidden', display: 'flex', flexDirection: 'row', alignItems: 'stretch', justifyContent: 'stretch', background: 'linear-gradient(120deg, #e3f2fd 60%, #fffde7 100%)' }}>
      <ComprehensiveReportDisplay report={analysis} />
    </div>
  );
}

export default App;
