import React, { useEffect, useState } from 'react';
import { Card, CardContent, Typography, Box, Button, Chip } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

function RagAnalysisPage({ ragAnalysis, onBack }) {
  const [ytExplanation, setYtExplanation] = useState(null);
  const [ytLoading, setYtLoading] = useState(false);
  const [ytError, setYtError] = useState(null);
  const [policySummaries, setPolicySummaries] = useState([]);

  useEffect(() => {
    if (!ragAnalysis) return;
    // Automatically fetch the canonical YouTube-style explanation for this report
    fetchYoutubeExplanation();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [ragAnalysis]);

  const fetchYoutubeExplanation = async () => {
    if (!ragAnalysis) return;
    setYtLoading(true);
    setYtError(null);
    try {
      const res = await fetch('http://localhost:8000/youtube-explanation', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(ragAnalysis),
      });
      if (!res.ok) throw new Error(`HTTP ${res.status}`);
      const data = await res.json();
      // backend returns { youtube_explanation: string, policy_summaries: [...] }
      setYtExplanation(data.youtube_explanation || data.youtubeExplanation || null);
      setPolicySummaries(data.policy_summaries || data.policySummaries || []);
    } catch (err) {
      setYtError(String(err.message || err));
      setYtExplanation(null);
      setPolicySummaries([]);
    } finally {
      setYtLoading(false);
    }
  };

  if (!ragAnalysis) {
    return (
      <Box sx={{ p: 4 }}>
        <Typography variant="h5">No RAG analysis data available.</Typography>
        <Button variant="contained" color="primary" onClick={onBack} sx={{ mt: 2 }}>
          <ArrowBackIcon sx={{ mr: 1 }} /> Back
        </Button>
      </Box>
    );
  }

  const {
    retrieved_docs = [],
    decision,
    confidence,
    reasoning,
    totalAnalyzed,
    totalDisplayed,
  } = ragAnalysis;

  const positiveCategories = [
    'safe content',
    'educational content',
    'entertainment content',
    'news content',
    'tutorials & how-to',
    'community & family-friendly',
    'artistic & creative expression',
    'positive social impact',
  ];

  // determine if the overall report is non-compliant
  const isNonCompliantDecision = String(decision || '').toLowerCase() !== 'compliant' && String(decision || '').length > 0;
  const explanationIndicatesNonCompliant = ytExplanation && /does not follow|not follow|violation|violations|not comply/i.test(ytExplanation);

  return (
    <Box sx={{ p: 5, background: 'linear-gradient(120deg, #e3f2fd 60%, #fffde7 100%)', minHeight: '100vh' }}>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <Button variant="outlined" color="secondary" sx={{ mr: 2, fontWeight: 'bold', borderRadius: 3 }} onClick={onBack}>
          ← Back
        </Button>
        <Typography variant="h4" color="primary" sx={{ fontWeight: 'bold' }}>
          RAG Policy Violation Explanations
        </Typography>
      </Box>

      {/* Prominent policy compliance banner restored: show when decision or explanation indicates non-compliance. If no policy rows returned, show the backend explanation as fallback. */}
      {(policySummaries && policySummaries.length > 0 && isNonCompliantDecision) || (isNonCompliantDecision || explanationIndicatesNonCompliant) ? (
        <Card sx={{ mb: 3, backgroundColor: '#ffebee', border: '2px solid #f44336', color: '#b71c1c', borderRadius: 2 }}>
          <CardContent>
            <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 1 }}>
              ⚠️ This video does not follow YouTube guidelines
            </Typography>
            {policySummaries && policySummaries.length > 0 ? (
              policySummaries.map((p, i) => (
                <Box key={i} sx={{ mb: 1 }}>
                  <Typography variant="body2" sx={{ fontWeight: 'bold' }}>{(p.category || '').replace(/_/g, ' ')}:</Typography>
                  <Typography variant="body2">{p.description}</Typography>
                  {p.action_required && <Typography variant="body2" sx={{ fontStyle: 'italic' }}>Action Required: {p.action_required}</Typography>}
                </Box>
              ))
            ) : (
              // fallback: show canonical explanation text returned by backend
              <Typography variant="body2">{ytExplanation || 'The model indicates this video is non-compliant.'}</Typography>
            )}
          </CardContent>
        </Card>
      ) : null}

      <Card sx={{ mb: 4, background: 'linear-gradient(90deg,#1976d2 60%,#21cbf3 100%)', color: '#fff', boxShadow: 3, borderRadius: 4 }}>
        <CardContent>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', gap: 2 }}>
            <Box>
              <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 2 }}>
                RAG Analysis Result
              </Typography>
              <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap', mb: 2 }}>
                <Chip label={`Decision: ${decision || 'N/A'}`} color="primary" sx={{ fontSize: '1.1rem' }} />
                <Chip label={`Confidence: ${confidence !== undefined ? confidence : 'N/A'}`} color="success" sx={{ fontSize: '1.1rem' }} />
              </Box>
              <Typography variant="body1"><b>Reasoning:</b> {reasoning || 'N/A'}</Typography>

              {/* Show the canonical YouTube-style explanation returned by the backend */}
              <Box sx={{ mt: 2 }}>
                {ytLoading ? (
                  <Typography variant="body2" color="info.light">Loading YouTube policy explanation...</Typography>
                ) : ytError ? (
                  <Typography variant="body2" color="error.light">Error loading explanation: {ytError}</Typography>
                ) : ytExplanation ? (
                  <Typography variant="body2" sx={{ mt: 1 }}><b>YouTube-style Summary:</b> {ytExplanation}</Typography>
                ) : (
                  <Typography variant="body2" color="info.light">No policy summary available.</Typography>
                )}
              </Box>
            </Box>

            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1, alignItems: 'flex-end' }}>
              <Button size="small" variant="outlined" color="inherit" onClick={fetchYoutubeExplanation}>Refresh Explanation</Button>
            </Box>
          </Box>

          <Box sx={{ mt: 2 }}>
            {totalAnalyzed !== undefined && (
              <Typography variant="body2" color="info.main">
                <b>Total Frames Analyzed by Model:</b> {totalAnalyzed}
              </Typography>
            )}
            {totalDisplayed !== undefined && (
              <Typography variant="body2" color="info.main">
                <b>Frames Displayed:</b> {totalDisplayed}
              </Typography>
            )}
            {totalAnalyzed !== undefined && totalDisplayed !== undefined && totalAnalyzed !== totalDisplayed && (
              <Typography variant="body2" color="warning.main">
                (Only the first {totalDisplayed} frames are shown based on your settings)
              </Typography>
            )}
          </Box>
        </CardContent>
      </Card>

      <Box sx={{ mb: 2 }}>
        <Typography variant="h5" color="primary" sx={{ fontWeight: 'bold', mb: 2 }}>
          Frame & Audio Details
        </Typography>
        {retrieved_docs && retrieved_docs.length > 0 ? (
          <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(420px, 1fr))', gap: 3 }}>
            {retrieved_docs.map((doc, idx) => {
              const conf = typeof doc.confidence === 'number' ? doc.confidence : (parseFloat(doc.confidence) || null);
              const dangerThreshold = 0.5;
              const warningThreshold = 0.75;
              let severity = 'safe';
              if (typeof conf === 'number') {
                if (conf < dangerThreshold) severity = 'danger';
                else if (conf < warningThreshold) severity = 'warning';
                else severity = 'safe';
              } else {
                severity = 'warning'; // unknown confidence -> caution
              }

              const cardBg = typeof doc.title === 'string' && doc.title.startsWith('Audio') ? '#e3f2fd' : '#f9fbe7';
              const severityStyles = {
                danger: { border: '2px solid rgba(244,67,54,0.25)', boxShadow: '0 6px 18px rgba(244,67,54,0.06)', background: 'linear-gradient(180deg, rgba(255,240,242,0.9), #fff)' },
                warning: { border: '2px solid rgba(255,193,7,0.18)', boxShadow: '0 4px 12px rgba(255,193,7,0.04)', background: 'linear-gradient(180deg, rgba(255,249,230,0.9), #fff)' },
                safe: { border: '2px solid rgba(76,175,80,0.06)', boxShadow: '0 2px 8px rgba(76,175,80,0.02)', background: cardBg },
              };

              const badge = (() => {
                if (severity === 'danger') return { icon: '⚠️', label: 'Review', color: '#7f1d1d' };
                if (severity === 'warning') return { icon: '⚠', label: 'Caution', color: '#92400e' };
                return { icon: '✅', label: 'OK', color: '#065f46' };
              })();

              return (
                <Card key={idx} sx={{ borderRadius: 3, p: 0, position: 'relative', ...severityStyles[severity] }} aria-label={`Frame ${doc.frame || idx} status ${severity} confidence ${conf !== null ? conf.toFixed(3) : 'n/a'}`}>
                  <CardContent sx={{ position: 'relative', background: 'transparent' }}>
                    {/* badge */}
                    <Box sx={{ position: 'absolute', right: 12, top: 12, display: 'flex', alignItems: 'center', gap: 1, px: 1.2, py: 0.5, borderRadius: 99, fontWeight: 700, backgroundColor: 'rgba(255,255,255,0.9)', zIndex: 10 }}>
                      <span style={{ fontSize: 16 }}>{badge.icon}</span>
                      <span style={{ color: badge.color, fontSize: 13 }}>{badge.label}</span>
                      <Chip label={conf !== null ? conf.toFixed(3) : 'n/a'} size="small" sx={{ ml: 1 }} />
                    </Box>

                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                      {getFrameIcon(doc.category)}
                      <Typography variant="subtitle1" sx={{ fontWeight: 'bold', ml: 1, color: typeof doc.title === 'string' && doc.title.startsWith('Audio') ? 'info.main' : 'success.main' }}>
                        {doc.title || doc.category || 'Frame'}
                      </Typography>
                    </Box>

                    {doc.frame !== undefined && (
                      <Typography variant="body2" sx={{ mb: 0.5 }}>
                        <b>Frame #:</b> {doc.frame}
                      </Typography>
                    )}

                    {doc.timestamp !== undefined && (
                      <Typography variant="body2" sx={{ mb: 0.5 }}>
                        <b>Timestamp:</b> {doc.timestamp}s
                      </Typography>
                    )}

                    <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', mb: 1 }}>
                      {doc.category && (
                        <Chip label={`Category: ${doc.category}`} color={doc.category === 'safe_content' ? 'success' : 'primary'} />
                      )}
                      {doc.confidence !== undefined && (
                        <Chip label={`Confidence: ${doc.confidence}`} color="success" />
                      )}
                    </Box>

                    {doc.reasoning && <Typography variant="body2" sx={{ mb: 0.5 }}><b>Reasoning:</b> {doc.reasoning}</Typography>}
                    {doc.blip && <Typography variant="body2" sx={{ mb: 0.5 }}><b>BLIP:</b> {doc.blip}</Typography>}
                    {doc.ocr && <Typography variant="body2" sx={{ mb: 0.5 }}><b>OCR:</b> {doc.ocr}</Typography>}
                    {doc.policy && <Typography variant="body2" sx={{ mb: 0.5 }}><b>Policy:</b> {doc.policy.description}</Typography>}
                    {doc.examples && doc.examples.length > 0 && <Typography variant="body2" sx={{ mb: 0.5 }}><b>Examples:</b> {Array.isArray(doc.examples) ? doc.examples.join(', ') : doc.examples}</Typography>}
                    {doc.action_required && <Typography variant="body2" sx={{ mb: 0.5 }}><b>Action Required:</b> {doc.action_required}</Typography>}
                    {doc.severity_indicators && doc.severity_indicators.length > 0 && <Typography variant="body2" sx={{ mb: 0.5 }}><b>Severity Indicators:</b> {doc.severity_indicators.join(', ')}</Typography>}
                    {doc.context_factors && doc.context_factors.length > 0 && <Typography variant="body2" sx={{ mb: 0.5 }}><b>Context Factors:</b> {doc.context_factors.join(', ')}</Typography>}
                    {doc.transcript && <Typography variant="body2" sx={{ mb: 0.5 }}><b>Transcript Excerpt:</b> {doc.transcript}</Typography>}
                    {doc.keywords && doc.keywords.length > 0 && <Typography variant="body2" sx={{ mb: 0.5 }}><b>Keywords:</b> {doc.keywords.join(', ')}</Typography>}
                  </CardContent>
                </Card>
              );
            })}
           </Box>
         ) : (
           <Typography>No RAG explanations found.</Typography>
         )}
      </Box>
    </Box>
  );

  // helper inside component so it can reference positiveCategories
  function getFrameIcon(category) {
    if (!category) return null;
    const cat = String(category).replace(/_/g, ' ').toLowerCase();
    if (positiveCategories.includes(cat)) {
      return <span style={{ color: '#43a047', fontWeight: 'bold', fontSize: 22 }}>✔️</span>;
    }
    return <span style={{ color: '#e53935', fontWeight: 'bold', fontSize: 22 }}>⚠️</span>;
  }
}

export default RagAnalysisPage;
