import React from 'react';
import { Card, CardContent, Typography, Box, Chip } from '@mui/material';
import AudiotrackIcon from '@mui/icons-material/Audiotrack';

function ComprehensiveReportDisplay({ report }) {
    if (!report) return null;

    // Parse model output and fallback
    const parsed = report.analysis_json ? report : null;
    const fields = parsed && parsed.analysis_json ? parsed.analysis_json : {};
    const fallback = report.parsedReport || {};

    // Statistics
    const violationCount = Array.isArray(fields.frame_analysis?.violation_categories)
        ? fields.frame_analysis.violation_categories.length
        : 0;
    const safeFrames = typeof fields.frame_analysis?.safe_frames === 'number' ? fields.frame_analysis.safe_frames : 0;
    const totalFrames = typeof fields.frame_analysis?.total_frames === 'number' ? fields.frame_analysis.total_frames : 0;
    const complianceRate = totalFrames > 0 ? Math.round((safeFrames / totalFrames) * 100) : 0;

    // Content extraction
    const transcript = fields.audio_analysis?.transcript || fields.transcript || fallback.transcript || '';
    const ocrText = fields.ocr_results?.text || fields.text_in_video?.text || fallback.ocr_text || '';
    const ocrItems = fields.ocr_results?.extracted_items || fields.text_in_video?.extracted_items || fallback.ocr_items || [];
    const blipDescriptions = fields.frame_analysis?.blip_descriptions || fallback.blip_descriptions || [];
    const copyrightCheck = report.copyright_check || null;

    // Transcript safety logic (define helpers)
    const badWords = fields.audio_analysis?.bad_words || [];
    const badPatterns = fields.audio_analysis?.bad_patterns || [];
    const isInappropriate = badWords.length > 0 || badPatterns.length > 0;
    let transcriptSafetyStatus = '';
    if (transcript === '') {
        transcriptSafetyStatus = 'No transcript available';
    } else if (isInappropriate) {
        if (badWords.length > 0) {
            transcriptSafetyStatus = 'Flagged: Inappropriate language detected';
        } else if (badPatterns.length > 0) {
            transcriptSafetyStatus = 'Close: Censored or partial inappropriate language detected';
        } else {
            transcriptSafetyStatus = 'Flagged: Inappropriate language detected';
        }
    } else {
        transcriptSafetyStatus = 'Safe: No inappropriate language detected';
    }

    // Power BI-style color palette
    const colors = {
        safe: '#43a047',
        violation: '#e53935',
        warning: '#fbc02d',
        info: '#1e88e5',
        cardBg: 'linear-gradient(120deg, #e3f2fd 60%, #fffde7 100%)',
        mainBg: 'linear-gradient(120deg, #f7f8fa 60%, #e3f2fd 100%)',
    };

    // Example chart data
    const chartData = [
        { name: 'Safe', value: safeFrames, color: colors.safe },
        { name: 'Violations', value: violationCount, color: colors.violation },
        { name: 'Total', value: totalFrames, color: colors.info },
    ];

    return (
        <Box sx={{ width: '100vw', height: '100vh', background: colors.mainBg, display: 'flex', flexDirection: 'row', alignItems: 'stretch', m: 0, p: 0, gap: 0 }}>
            {/* Left: Transcript Safety Card */}
            <Box sx={{ flex: 1, width: '100%', height: '100%', background: colors.cardBg, borderRadius: 0, boxShadow: 0, p: 0, overflow: 'auto', display: 'flex', flexDirection: 'column', justifyContent: 'stretch' }}>
                <Card sx={{ boxShadow: 2, borderRadius: 0, mb: 0, background: '#fff', height: '100%', width: '100%' }}>
                    <CardContent sx={{ height: '100%', width: '100%' }}>
                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                            <AudiotrackIcon color="info" sx={{ fontSize: 32, mr: 2 }} />
                            <Typography variant="h6" sx={{ fontWeight: 'bold', color: colors.info }}>Transcript Safety</Typography>
                        </Box>
                        <Chip label={transcriptSafetyStatus} color={transcriptSafetyStatus.startsWith('Safe') ? 'success' : transcriptSafetyStatus.startsWith('Close') ? 'warning' : transcriptSafetyStatus.startsWith('No') ? 'warning' : 'error'} sx={{ fontWeight: 'bold', fontSize: 16, mb: 2 }} />
                        {isInappropriate && (badWords.length > 0 || badPatterns.length > 0) && (
                            <Typography variant="body2" sx={{ color: transcriptSafetyStatus.startsWith('Close') ? colors.warning : colors.violation, mt: 1 }}>
                                {badWords.length > 0 && `Bad words: ${badWords.join(', ')}`}
                                {badPatterns.length > 0 && `Close matches or censored forms detected.`}
                            </Typography>
                        )}
                    </CardContent>
                </Card>
                {/* Add more cards for extracted content, OCR, copyright, etc. */}
            </Box>
            {/* Center: Power BI-style Analysis Cards & Chart */}
            <Box sx={{ flex: 2, width: '100%', height: '100%', background: colors.cardBg, borderRadius: 0, boxShadow: 0, p: 0, overflow: 'auto', display: 'flex', flexDirection: 'column', justifyContent: 'stretch' }}>
                <Card sx={{ boxShadow: 2, borderRadius: 0, mb: 0, background: '#fff', height: '100%', width: '100%' }}>
                    <CardContent sx={{ height: '100%', width: '100%' }}>
                        <Typography variant="h5" sx={{ fontWeight: 'bold', color: colors.info, mb: 2 }}>Model Analysis Overview</Typography>
                        <Box sx={{ display: 'flex', gap: 3, mb: 2, width: '100%' }}>
                            <Card sx={{ flex: 1, background: colors.safe, color: '#fff', boxShadow: 1, borderRadius: 2, p: 2, height: '100%' }}>
                                <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>Safe Frames</Typography>
                                <Typography variant="h4" sx={{ fontWeight: 'bold' }}>{safeFrames}</Typography>
                            </Card>
                            <Card sx={{ flex: 1, background: colors.violation, color: '#fff', boxShadow: 1, borderRadius: 2, p: 2, height: '100%' }}>
                                <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>Violations</Typography>
                                <Typography variant="h4" sx={{ fontWeight: 'bold' }}>{violationCount}</Typography>
                            </Card>
                            <Card sx={{ flex: 1, background: colors.info, color: '#fff', boxShadow: 1, borderRadius: 2, p: 2, height: '100%' }}>
                                <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>Total Frames</Typography>
                                <Typography variant="h4" sx={{ fontWeight: 'bold' }}>{totalFrames}</Typography>
                            </Card>
                        </Box>
                        {/* Example: Pie chart (replace with Recharts/Chart.js for interactivity) */}
                        <Box sx={{ mt: 2, mb: 2, width: '100%' }}>
                            <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 1 }}>Compliance Distribution</Typography>
                            {/* Placeholder for chart - replace with <PieChart> or <BarChart> for real data */}
                            <Box sx={{ width: '100%', height: 220, background: '#e3f2fd', borderRadius: 2, boxShadow: 1, display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', fontSize: 22, color: colors.info }}>
                                [Chart Placeholder]
                            </Box>
                        </Box>
                    </CardContent>
                </Card>
                {/* Add more cards for frame details, RAG explanations, etc. */}
            </Box>
        </Box>
    );
}

export default ComprehensiveReportDisplay;
