import React, { useState } from 'react';

function SidebarUpload({ onFileChange, onAnalyze, loading, error }) {
  return (
    <div style={{ padding: '24px 16px', background: 'transparent' }}>
      <h3 style={{ color: '#1976d2', marginBottom: 12 }}>Upload Video</h3>
      <input type="file" accept="video/*" onChange={onFileChange} style={{ marginBottom: 12 }} />
      <button onClick={onAnalyze} disabled={loading} style={{ background: '#1976d2', color: '#fff', border: 'none', padding: '8px 16px', borderRadius: 4, cursor: 'pointer', fontWeight: 600 }}>
        {loading ? 'Analyzing...' : 'Analyze'}
      </button>
      {error && <div style={{ color: 'red', marginTop: 8 }}>{error}</div>}
    </div>
  );
}

export default function ComprehensiveReportDisplay({ report }) {
  const [selectedFile, setSelectedFile] = useState(null);
  const [analysis, setAnalysis] = useState(report || null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setError('');
    setAnalysis(null);
  };

  const handleAnalyze = async () => {
    if (!selectedFile) {
      setError('Please select a video file');
      return;
    }
    setLoading(true);
    setError('');
    setAnalysis(null);
    // Simulate analysis (replace with real API call)
    setTimeout(() => {
      setAnalysis({ analysis_json: { frame_analysis: { safe_frames: 10, violation_categories: ['adult_content'], total_frames: 12 }, audio_analysis: { transcript: 'Sample transcript', bad_words: [], bad_patterns: [] } } });
      setLoading(false);
    }, 2000);
  };

  return (
    <div style={{ width: '100vw', height: '100vh', display: 'flex', flexDirection: 'row', background: 'linear-gradient(120deg, #e3f2fd 60%, #fffde7 100%)' }}>
      <div style={{ width: 320, background: 'rgba(25, 118, 210, 0.08)', borderRight: '1px solid #e3f2fd', minHeight: '100vh', boxShadow: '2px 0 8px rgba(0,0,0,0.04)', zIndex: 2 }}>
        <SidebarUpload onFileChange={handleFileChange} onAnalyze={handleAnalyze} loading={loading} error={error} />
        {/* ...existing sidebar menu code... */}
      </div>
      <div style={{ flex: 1, height: '100vh', overflow: 'auto', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        {!analysis ? (
          <h2 style={{ color: '#1976d2', fontWeight: 600 }}>Please upload a video to start analysis.</h2>
        ) : (
          // ...existing report rendering code...
          <div>
            <h2>Analysis Results</h2>
            <pre>{JSON.stringify(analysis, null, 2)}</pre>
          </div>
        )}
      </div>
    </div>
  );
}
