import React from 'react';

export default function ComprehensiveReportDisplay({ report }) {
  return (
    <div style={{ width: '100vw', height: '100vh', display: 'flex', flexDirection: 'row', background: 'linear-gradient(120deg, #e3f2fd 60%, #fffde7 100%)' }}>
      {/* Sidebar is now handled by App_Clean.js, so no upload field here */}
      <div style={{ flex: 1, height: '100vh', overflow: 'auto', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        {!report ? (
          <h2 style={{ color: '#1976d2', fontWeight: 600 }}>Please upload a video to start analysis.</h2>
        ) : (
          <div>
            <h2>Analysis Results</h2>
            <pre>{JSON.stringify(report, null, 2)}</pre>
          </div>
        )}
      </div>
    </div>
  );
}
