import React, { useState, useEffect } from 'react';
import VideoUpload from './VideoUpload';
import ComprehensiveReportDisplay from './ComprehensiveReportDisplay';
import RagAnalysisPage from './RagAnalysisPage';
import SettingsPage from './SettingsPage';
import UploadHistoryPage from './UploadHistoryPage';
import FrameDetailsPage from './FrameDetailsPage';
import {
  Container,
  AppBar,
  Toolbar,
  Typography,
  Box,
  Card,
  CardContent,
  Button,
  CircularProgress,
  Alert,
  LinearProgress,
  Paper,
  Grid
} from '@mui/material';
import CloudUpload from '@mui/icons-material/CloudUpload';
import VideoLibrary from '@mui/icons-material/VideoLibrary';
import CheckCircle from '@mui/icons-material/CheckCircle';
import Analytics from '@mui/icons-material/Analytics';

const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [progress, setProgress] = useState(0);
  const [showRagPage, setShowRagPage] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [ragAnalysis, setRagAnalysis] = useState(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showFrameDetails, setShowFrameDetails] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  const [parsedReport, setParsedReport] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('testing');
  const [copyrightStatus, setCopyrightStatus] = useState(null);
  const [frameSettings, setFrameSettings] = useState({
    frameCount: 1,
    frameInterval: 0.5,
    frameResolution: 'auto',
    frameFormat: 'jpg',
    frameStart: 0,
    frameEnd: null,
    frameSampling: 'interval',
  });

  useEffect(() => {
    testConnection();
  }, []);

  const testConnection = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      if (response.ok) {
        setConnectionStatus('connected');
      } else {
        setConnectionStatus('disconnected');
      }
    } catch (err) {
      setConnectionStatus('disconnected');
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.type.startsWith('video/')) {
        setSelectedFile(file);
        setError('');
        setAnalysis(null);
      } else {
        setError('Please select a valid video file');
        setSelectedFile(null);
      }
    }
  };

  const handleAnalyze = async () => {
    if (!selectedFile) {
      setError('Please select a video file');
      return;
    }
    setLoading(true);
    setError('');
    setAnalysis(null);
    setCopyrightStatus(null);
    try {
      // Build params based on extraction mode
      const params = new URLSearchParams();
      // ...your analysis logic here...
    } catch (err) {
      setError(`Analysis failed: ${err.message}`);
    } finally {
      setLoading(false);
    }
  }

  // Always show the dashboard full screen with the report
  return (
    <Box sx={{ width: '100vw', height: '100vh', m: 0, p: 0, overflow: 'hidden', display: 'flex', flexDirection: 'row', alignItems: 'stretch', justifyContent: 'stretch', background: 'linear-gradient(120deg, #e3f2fd 60%, #fffde7 100%)' }}>
      <ComprehensiveReportDisplay report={analysis} />
    </Box>
  );
}

export default App;
